INSERT INTO `config_info` VALUES (1850, 'ruoyi-gateway.yml', 'DEFAULT_GROUP', '# 安全配置\nsecurity:\n  # 防止XSS攻击\n  xss:\n    enabled: true\n    excludeUrls:\n      - /system/notice\n  # 不校验白名单\n  ignore:\n    whites:\n      - /auth/code\n      - /auth/logout\n      - /auth/login\n      - /auth/binding/*\n      - /auth/social/callback\n      - /auth/register\n      - /auth/tenant/list\n      - /resource/sms/code\n      - /*/v3/api-docs\n      - /*/error\n      - /csrf\n      - /baseplat/evcs/**\n      - /userplat/evcs/**\n      - /mp/**\n\nspring:\n  cloud:\n    # 网关配置\n    gateway:\n      # 打印请求日志(自定义)\n      requestLog: true\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true\n      routes:\n        # 认证中心\n        - id: ruoyi-auth\n          uri: lb://ruoyi-auth\n          predicates:\n            - Path=/auth/**\n          filters:\n            - StripPrefix=1\n        # 代码生成\n        - id: ruoyi-gen\n          uri: lb://ruoyi-gen\n          predicates:\n            - Path=/tool/**\n          filters:\n            - StripPrefix=1\n        # 系统模块\n        - id: ruoyi-system\n          uri: lb://ruoyi-system\n          predicates:\n            - Path=/system/**,/monitor/**\n          filters:\n            - StripPrefix=1\n        # 资源服务\n        - id: ruoyi-resource\n          uri: lb://ruoyi-resource\n          predicates:\n            - Path=/resource/**\n          filters:\n            - StripPrefix=1\n        # 演示服务\n        - id: ruoyi-demo\n          uri: lb://ruoyi-demo\n          predicates:\n            - Path=/demo/**\n          filters:\n            - StripPrefix=1\n        # MQ演示服务\n        - id: ruoyi-stream-mq\n          uri: lb://ruoyi-stream-mq\n          predicates:\n            - Path=/stream-mq/**\n          filters:\n            - StripPrefix=1\n        # 充电-基础平台\n        - id: omind-baseplat\n          uri: lb://omind-baseplat\n          predicates:\n            - Path=/baseplat/**\n          filters:\n            - StripPrefix=1\n        # 充电-设备平台\n        - id: omind-deviceplat\n          uri: lb://omind-simplat #http://172.25.0.121:9803 \n          predicates:\n            - Path=/deviceplat/**\n          filters:\n            - StripPrefix=1\n        # 充电-用户平台\n        - id: omind-userplat\n          uri: lb://omind-userplat\n          predicates:\n            - Path=/userplat/**\n          filters:\n            - StripPrefix=1\n        # 充电-客户端\n        - id: omind-mp\n          uri: lb://omind-mp\n          predicates:\n            - Path=/mp/**\n          filters:\n            - StripPrefix=1\n\n    # sentinel 配置\n    sentinel:\n      filter:\n        enabled: false\n      # nacos配置持久化\n      datasource:\n        ds1:\n          nacos:\n            server-addr: ${spring.cloud.nacos.server-addr}\n            dataId: sentinel-${spring.application.name}.json\n            groupId: ${spring.cloud.nacos.config.group}\n            namespace: ${spring.profiles.active}\n            data-type: json\n            rule-type: gw-flow\n', '523e21c2c27f1e87200fd8cadf302875', '2025-03-14 10:54:58', '2025-03-14 11:00:57', NULL, '172.25.0.1', '', 'local', NULL, NULL, NULL, 'yaml', NULL, '');
