INSERT INTO `config_info` VALUES (1858, 'application-common.yml', 'DEFAULT_GROUP', 'server:\n  # undertow 配置\n  undertow:\n    # HTTP post内容的最大大小。当值为-1时，默认值为大小是无限的\n    max-http-post-size: -1\n    # 以下的配置会影响buffer,这些buffer会用于服务器连接的IO操作,有点类似netty的池化内存管理\n    # 每块buffer的空间大小,越小的空间被利用越充分\n    buffer-size: 512\n    # 是否分配的直接内存\n    direct-buffers: true\n    threads:\n      # 设置IO线程数, 它主要执行非阻塞的任务,它们会负责多个连接, 默认设置每个CPU核心一个线程\n      io: 8\n      # 阻塞任务线程池, 当执行类似servlet请求阻塞操作, undertow会从这个线程池中取得线程,它的值设置取决于系统的负载\n      worker: 256\n\ndubbo:\n  application:\n    # 关闭qos端口避免单机多生产者端口冲突 如需使用自行开启\n    qos-enable: false\n    serialize-check-status: WARN\n  protocol:\n    # 如需使用 Triple 3.0 新协议 可查看官方文档\n    # 使用 dubbo 协议通信\n    name: dubbo\n    # dubbo 协议端口(-1表示自增端口,从20880开始)\n    port: -1\n    # 指定dubbo协议注册ip\n    # host: 192.168.0.100\n  # 消费者相关配置\n  consumer:\n    # 超时时间\n    timeout: 3000\n  scan:\n    # 接口实现类扫描\n    base-packages: org.dromara.**.dubbo\n  # 自定义配置\n  custom:\n    # 全局请求log\n    request-log: true\n    # info 基础信息 param 参数信息 full 全部\n    log-level: info\n\nspring:\n  # 资源信息\n  messages:\n    # 国际化资源文件路径\n    basename: i18n/messages\n  servlet:\n    multipart:\n      # 整个请求大小限制\n      max-request-size: 20MB\n      # 上传单个文件大小限制\n      max-file-size: 10MB\n  mvc:\n    format:\n      date-time: yyyy-MM-dd HH:mm:ss\n  #jackson配置\n  jackson:\n    # 日期格式化\n    date-format: yyyy-MM-dd HH:mm:ss\n    serialization:\n      # 格式化输出\n      INDENT_OUTPUT: false\n      # 忽略无法转换的对象\n      fail_on_empty_beans: false\n    deserialization:\n      # 允许对象忽略json中不存在的属性\n      fail_on_unknown_properties: false\n  cloud:\n    # sentinel 配置\n    sentinel:\n      # sentinel 开关\n      enabled: true\n      # 取消控制台懒加载\n      eager: true\n      transport:\n        # dashboard控制台服务名 用于服务发现\n        # 如无此配置将默认使用下方 dashboard 配置直接注册\n        server-name: ruoyi-sentinel-dashboard\n        # 客户端指定注册的ip 用于多网卡ip不稳点使用\n        # client-ip:\n        # 控制台地址 从1.3.0开始使用 server-name 注册\n        # dashboard: localhost:8718\n\n  # redis通用配置 子服务可以自行配置进行覆盖\n  data:\n    redis:\n      host: localhost\n      port: 6379\n      # 密码(如没有密码请注释掉)\n      # password: omind2023\n      database: 0\n      timeout: 10s\n      ssl.enabled: false\n\n# redisson 配置\nredisson:\n  # redis key前缀\n  keyPrefix:\n  # 线程池数量\n  threads: 4\n  # Netty线程池数量\n  nettyThreads: 8\n  # 单节点配置\n  singleServerConfig:\n    # 客户端名称\n    clientName: ${spring.application.name}\n    # 最小空闲连接数\n    connectionMinimumIdleSize: 8\n    # 连接池大小\n    connectionPoolSize: 32\n    # 连接空闲超时，单位：毫秒\n    idleConnectionTimeout: 10000\n    # 命令等待超时，单位：毫秒\n    timeout: 3000\n    # 发布和订阅连接池大小\n    subscriptionConnectionPoolSize: 50\n\n# 分布式锁 lock4j 全局配置\nlock4j:\n  # 获取分布式锁超时时间，默认为 3000 毫秒\n  acquire-timeout: 3000\n  # 分布式锁的超时时间，默认为 30 秒\n  expire: 30000\n\n# 暴露监控端点\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \'*\'\n  endpoint:\n    health:\n      show-details: ALWAYS\n    logfile:\n      external-file: ./logs/${spring.application.name}/console.log\n\n# 日志配置\nlogging:\n  level:\n    org.springframework: warn\n    org.apache.dubbo: warn\n    com.alibaba.nacos: warn\n    tech.powerjob.worker.background: warn\n  config: classpath:logback-plus.xml\n\n# Sa-Token配置\nsa-token:\n  # token名称 (同时也是cookie名称)\n  token-name: Authorization\n  # 开启内网服务调用鉴权(不允许越过gateway访问内网服务 保障服务安全)\n  check-same-token: true\n  # 是否允许同一账号并发登录 (为true时允许一起登录, 为false时新登录挤掉旧登录)\n  is-concurrent: true\n  # 在多人登录同一账号时，是否共用一个token (为true时所有登录共用一个token, 为false时每次登录新建一个token)\n  is-share: false\n  # jwt秘钥\n  jwt-secret-key: abcdefghijklmnopqrstuvwxyz\n\n# MyBatisPlus配置\n# https://baomidou.com/config/\nmybatis-plus:\n  # 不支持多包, 如有需要可在注解配置 或 提升扫包等级\n  # 例如 com.**.**.mapper\n  mapperPackage: org.dromara.**.mapper\n  # 对应的 XML 文件位置\n  mapperLocations: classpath*:mapper/**/*Mapper.xml\n  # 实体扫描，多个package用逗号或者分号分隔\n  typeAliasesPackage: org.dromara.**.domain\n  global-config:\n    dbConfig:\n      # 主键类型\n      # AUTO 自增 NONE 空 INPUT 用户输入 ASSIGN_ID 雪花 ASSIGN_UUID 唯一 UUID\n      # 如需改为自增 需要将数据库表全部设置为自增\n      idType: ASSIGN_ID\n\n# 数据加密\nmybatis-encryptor:\n  # 是否开启加密\n  enable: false\n  # 默认加密算法\n  algorithm: BASE64\n  # 编码方式 BASE64/HEX。默认BASE64\n  encode: BASE64\n  # 安全秘钥 对称算法的秘钥 如：AES，SM4\n  password:\n  # 公私钥 非对称算法的公私钥 如：SM2，RSA\n  publicKey:\n  privateKey:\n\n# api接口加密\napi-decrypt:\n  # 是否开启全局接口加密\n  enabled: true\n  # AES 加密头标识\n  headerFlag: encrypt-key\n  # 公私钥 非对称算法的公私钥 如：SM2，RSA 使用者请自行更换\n  publicKey: MFwwDQYJKoZIhvcNAQEBBQADSwAwSAJBAKoR8mX0rGKLqzcWmOzbfj64K8ZIgOdHnzkXSOVOZbFu/TJhZ7rFAN+eaGkl3C4buccQd/EjEsj9ir7ijT7h96MCAwEAAQ==\n  privateKey: \n\n# 接口文档配置\nspringdoc:\n  api-docs:\n    # 是否开启接口文档\n    enabled: true\n  #  swagger-ui:\n  #    # 持久化认证数据\n  #    persistAuthorization: true\n  info:\n    # 标题\n    title: \'标题：RuoYi-Cloud-Plus微服务权限管理系统_接口文档\'\n    # 描述\n    description: \'描述：微服务权限管理系统, 具体包括XXX,XXX模块...\'\n    # 版本\n    version: \'版本号：系统版本...\'\n    # 作者信息\n    contact:\n      name: Lion Li\n      email: crazylionli@163.com\n      url: https://gitee.com/dromara/RuoYi-Cloud-Plus\n  components:\n    # 鉴权方式配置\n    security-schemes:\n      apiKey:\n        type: APIKEY\n        in: HEADER\n        name: ${sa-token.token-name}\n\n# seata配置\nseata:\n  # 是否启用\n  enabled: false\n  # Seata 应用编号，默认为应用名\n  application-id: ${spring.application.name}\n  # Seata 事务组编号，用于 TC 集群名\n  tx-service-group: ${spring.application.name}-group\n\n# 多租户配置\ntenant:\n  # 是否开启\n  enable: true\n  # 排除表\n  excludes:\n    - sys_menu\n    - sys_tenant\n    - sys_tenant_package\n    - sys_role_dept\n    - sys_role_menu\n    - sys_user_post\n    - sys_user_role\n    - sys_client\n    - sys_area\n', '5ea3f9900a3e9232288a8527f376df98', '2025-03-14 10:56:26', '2025-03-14 11:00:57', NULL, '172.25.0.1', '', 'local', NULL, NULL, NULL, 'yaml', NULL, '');
INSERT INTO `config_info` VALUES (1861, 'datasource.yml', 'DEFAULT_GROUP', 'datasource:\n  system-master:\n    # jdbc 所有参数配置参考 https://lionli.blog.csdn.net/article/details/122018562\n    # rewriteBatchedStatements=true 批处理优化 大幅提升批量插入更新删除性能\n    url: jdbc:mysql://localhost:3306/ry-cloud?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8&rewriteBatchedStatements=true&allowPublicKeyRetrieval=true\n    username: omind\n    password: omind2023\n  gen:\n    url: jdbc:mysql://localhost:3306/ry-cloud?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8&rewriteBatchedStatements=true&allowPublicKeyRetrieval=true\n    username: omind\n    password: omind2023\n  job:\n    url: jdbc:mysql://localhost:3306/ry-job?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8&rewriteBatchedStatements=true&allowPublicKeyRetrieval=true\n    username: omind\n    password: omind2023\n  omind-base-plat:\n    url: jdbc:mysql://localhost:3306/omind-base-plat?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8&rewriteBatchedStatements=true&allowPublicKeyRetrieval=true\n    username: omind\n    password: omind2023\n  omind-user-plat:\n    url: jdbc:mysql://localhost:3306/omind-user-plat?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=true&serverTimezone=GMT%2B8&rewriteBatchedStatements=true&allowPublicKeyRetrieval=true\n    username: omind\n    password: omind2023\n#  system-oracle:\n#    url: jdbc:oracle:thin:@//localhost:1521/XE\n#    username: ROOT\n#    password: password\n#  system-postgres:\n#    url: jdbc:postgresql://localhost:5432/postgres?useUnicode=true&characterEncoding=utf8&useSSL=true&autoReconnect=true&reWriteBatchedInserts=true\n#    username: root\n#    password: password\n\nspring:\n  datasource:\n    type: com.zaxxer.hikari.HikariDataSource\n    # 动态数据源文档 https://www.kancloud.cn/tracy5546/dynamic-datasource/content\n    dynamic:\n      # 性能分析插件(有性能损耗 不建议生产环境使用)\n      p6spy: false\n      # 开启seata代理，开启后默认每个数据源都代理，如果某个不需要代理可单独关闭\n      seata: ${seata.enabled}\n      # 严格模式 匹配不到数据源则报错\n      strict: true\n      hikari:\n        # 最大连接池数量\n        maxPoolSize: 20\n        # 最小空闲线程数量\n        minIdle: 10\n        # 配置获取连接等待超时的时间\n        connectionTimeout: 30000\n        # 校验超时时间\n        validationTimeout: 5000\n        # 空闲连接存活最大时间，默认10分钟\n        idleTimeout: 600000\n        # 此属性控制池中连接的最长生命周期，值0表示无限生命周期，默认30分钟\n        maxLifetime: 1800000\n        # 连接测试query（配置检测连接是否有效）\n        connectionTestQuery: SELECT 1\n        # 多久检查一次连接的活性\n        keepaliveTime: 30000\n', 'aba0368f2dd2a630d82a71f5f1a4c0ff', '2025-03-14 11:00:57', '2025-03-14 11:00:57', NULL, '172.25.0.1', '', 'local', NULL, NULL, NULL, 'yaml', NULL, '');
INSERT INTO `config_info` VALUES (1866, 'omind-mq.yml', 'DEFAULT_GROUP', '--- # rabbitmq 配置\nspring:\n  rabbitmq:\n    host: localhost\n    port: 5672\n    username: omind\n    password: omind2023\n  cloud:\n    # https://docs.spring.io/spring-cloud-stream/reference/rabbit/rabbit_overview/receiving-batch.html\n    function:\n      # 重点配置 与 binding 名与消费者对应\n      definition: stationPriceSender;priceSender;realtimeData;chargeOrderData;heartBeat\n    stream:\n      function:\n        bindings:\n          realtimeData-in-0: realtimeData-in\n          realtimeData-out-0: realtimeData-out\n          chargeOrderData-in-0: chargeOrderData-in\n          chargeOrderData-out-0: chargeOrderData-out\n          priceSender-in-0: priceSender-in\n          priceSender-out-0: priceSender-out\n          stationPriceSender-in-0: stationPriceSender-in\n          stationPriceSender-out-0: stationPriceSender-out\n          heartBeat-in-0: heartBeat-in\n          heartBeat-out-0: heartBeat-out\n      default-binder: rabbit\n      bindings:\n        realtimeData-in:\n          binder: rabbit\n          destination: realtimeData-topic\n          content-type: application/json\n          group: device-group\n          consumer:\n            batch-mode: true\n            concurrency: 1 # 初始/最少/空闲时 消费者数量。默认1\n        realtimeData-out:\n          binder: rabbit\n          destination: realtimeData-topic\n          content-type: application/json\n          group: device-group\n        chargeOrderData-in:\n          binder: rabbit\n          destination: chargeOrderData-topic\n          content-type: application/json\n          group: device-group\n        chargeOrderData-out:\n          binder: rabbit\n          destination: chargeOrderData-topic\n          content-type: application/json\n          group: device-group\n        priceSender-in:\n          binder: rabbit\n          destination: priceSender-topic\n          content-type: application/json\n          group: device-group\n        priceSender-out:\n          binder: rabbit\n          destination: priceSender-topic\n          content-type: application/json\n          group: device-group\n        stationPriceSender-in:\n          binder: rabbit\n          destination: stationPriceSender-topic\n          content-type: application/json\n          group: device-group\n        stationPriceSender-out:\n          binder: rabbit\n          destination: stationPriceSender-topic\n          content-type: application/json\n          group: device-group\n        heartBeat-in:\n          binder: rabbit\n          destination: heartBeat-topic\n          content-type: application/json\n          group: device-group\n          consumer:\n            batch-mode: true\n            concurrency: 1 # 初始/最少/空闲时 消费者数量。默认1\n        heartBeat-out:\n          binder: rabbit\n          destination: heartBeat-topic\n          content-type: application/json\n          group: device-group\n      rabbit:\n        bindings:\n          realtimeData-in:\n            consumer:\n              enable-batching: true\n              batch-size: 50\n              receive-timeout: 2000\n            producer:\n              batching-enabled: true\n              batch-size: 50\n              batch-buffer-limit: 2000\n              batch-timeout: 5000\n          heartBeat-in:\n            consumer:\n              enable-batching: true\n              batch-size: 50\n              receive-timeout: 2000\n            producer:\n              batching-enabled: true\n              batch-size: 50\n              batch-buffer-limit: 2000\n              batch-timeout: 5000', '7b37f0303d975d6ea513f86bff2b7ed3', '2025-03-14 11:00:57', '2025-03-14 11:46:58', 'nacos', '172.25.0.1', '', 'local', '', '', '', 'yaml', '', '');
INSERT INTO `config_info` VALUES (1870, 'omind-user-mq.yml', 'DEFAULT_GROUP', '--- # rabbitmq 配置\nspring:\n  rabbitmq:\n    host: localhost\n    port: 5672\n    username: omind\n    password: omind2023\n  cloud:\n    # https://docs.spring.io/spring-cloud-stream/reference/rabbit/rabbit_overview/receiving-batch.html\n    function:\n      # 重点配置 与 binding 名与消费者对应\n      definition: upStationStatus;upEquipChargeStatus;upChargeOrderInfo\n    stream:\n      function:\n        bindings:\n          upStationStatus-in-0: upStationStatus-in\n          upStationStatus-out-0: upStationStatus-out\n          upEquipChargeStatus-in-0: upEquipChargeStatus-in\n          upEquipChargeStatus-out-0: upEquipChargeStatus-out\n          upChargeOrderInfo-in-0: upChargeOrderInfo-in\n          upChargeOrderInfo-out-0: upChargeOrderInfo-out\n      default-binder: rabbit\n      bindings:\n        upStationStatus-in:\n          binder: rabbit\n          destination: upStationStatus-topic\n          content-type: application/json\n          group: userplat-group\n        upStationStatus-out:\n          binder: rabbit\n          destination: upStationStatus-topic\n          content-type: application/json\n          group: userplat-group\n        upEquipChargeStatus-in:\n          binder: rabbit\n          destination: upEquipChargeStatus-topic\n          content-type: application/json\n          group: userplat-group\n        upEquipChargeStatus-out:\n          binder: rabbit\n          destination: upEquipChargeStatus-topic\n          content-type: application/json\n          group: userplat-group\n        upChargeOrderInfo-in:\n          binder: rabbit\n          destination: upChargeOrderInfo-topic\n          content-type: application/json\n          group: userplat-group\n        upChargeOrderInfo-out:\n          binder: rabbit\n          destination: upChargeOrderInfo-topic\n          content-type: application/json\n          group: userplat-group', '045e6b1b7218ee82c05070aff69b27ca', '2025-03-14 11:00:57', '2025-03-14 15:36:15', 'nacos', '172.25.0.1', '', 'local', '', '', '', 'yaml', '', '');
