INSERT INTO `his_config_info` VALUES (0, 1904, 'ruoyi-resource.yml', 'DEFAULT_GROUP', '', 'spring:\n  datasource:\n    dynamic:\n      # 设置默认的数据源或者数据源组,默认值即为 master\n      primary: master\n      datasource:\n        # 主库数据源\n        master:\n          type: ${spring.datasource.type}\n          driver-class-name: com.mysql.cj.jdbc.Driver\n          url: ${datasource.system-master.url}\n          username: ${datasource.system-master.username}\n          password: ${datasource.system-master.password}\n#        oracle:\n#          type: ${spring.datasource.type}\n#          driverClassName: oracle.jdbc.OracleDriver\n#          url: ${datasource.system-oracle.url}\n#          username: ${datasource.system-oracle.username}\n#          password: ${datasource.system-oracle.password}\n#          hikari:\n#            connectionTestQuery: SELECT 1 FROM DUAL\n#        postgres:\n#          type: ${spring.datasource.type}\n#          driverClassName: org.postgresql.Driver\n#          url: ${datasource.system-postgres.url}\n#          username: ${datasource.system-postgres.username}\n#          password: ${datasource.system-postgres.password}\n\nwebsocket:\n  # 如果关闭 需要和前端开关一起关闭\n  enabled: true\n  # 路径\n  path: /websocket\n  # 设置访问源地址\n  allowedOrigins: \'*\'\n\nmail:\n  enabled: false\n  host: smtp.163.com\n  port: 465\n  # 是否需要用户名密码验证\n  auth: true\n  # 发送方，遵循RFC-822标准\n  from: xxx@163.com\n  # 用户名（注意：如果使用foxmail邮箱，此处user为qq号）\n  user: xxx@163.com\n  # 密码（注意，某些邮箱需要为SMTP服务单独设置密码，详情查看相关帮助）\n  pass: xxxxxxxxxx\n  # 使用 STARTTLS安全连接，STARTTLS是对纯文本通信协议的扩展。\n  starttlsEnable: true\n  # 使用SSL安全连接\n  sslEnable: true\n  # SMTP超时时长，单位毫秒，缺省值不超时\n  timeout: 0\n  # Socket连接超时值，单位毫秒，缺省值不超时\n  connectionTimeout: 0\n\n# sms 短信 支持 阿里云 腾讯云 云片 等等各式各样的短信服务商\n# https://wind.kim/doc/start 文档地址 各个厂商可同时使用\nsms:\n  # 阿里云 dysmsapi.aliyuncs.com\n  alibaba:\n    #请求地址 默认为 dysmsapi.aliyuncs.com 如无特殊改变可以不用设置\n    requestUrl: dysmsapi.aliyuncs.com\n    #阿里云的accessKey\n    accessKeyId: xxxxxxx\n    #阿里云的accessKeySecret\n    accessKeySecret: xxxxxxx\n    #短信签名\n    signature: 测试\n  tencent:\n    #请求地址默认为 sms.tencentcloudapi.com 如无特殊改变可不用设置\n    requestUrl: sms.tencentcloudapi.com\n    #腾讯云的accessKey\n    accessKeyId: xxxxxxx\n    #腾讯云的accessKeySecret\n    accessKeySecret: xxxxxxx\n    #短信签名\n    signature: 测试\n    #短信sdkAppId\n    sdkAppId: appid\n    #地域信息默认为 ap-guangzhou 如无特殊改变可不用设置\n    territory: ap-guangzhou\n', '9d58efcf9e32f5cc428fa1182e0f8bdb', '2025-03-14 10:54:58', '2025-03-14 10:54:58', NULL, '172.25.0.1', 'I', 'local', '');
