INSERT INTO `his_config_info` VALUES (1854, 2005, 'omind-baseplat.yml', 'DEFAULT_GROUP', '', 'spring:\n  datasource:\n    dynamic:\n      # 设置默认的数据源或者数据源组,默认值即为 master\n      primary: master\n      seata: false\n      datasource:\n        # 主库数据源\n        master:\n          type: ${spring.datasource.type}\n          driver-class-name: com.mysql.cj.jdbc.Driver\n          url: ${datasource.omind-base-plat.url}\n          username: ${datasource.omind-base-plat.username}\n          password: ${datasource.omind-base-plat.password}\n\nforest:\n  backend: okhttp3\n  max-retry-count: 3 #请求失败后重试次数，默认为0次不重试\n  max-retry-interval: 1000 #重试间隔时间\n  connect-timeout: 3000 #链接超时时间\n  timeout: 3000  # 请求超时时间\n  ## 日志总开关，打开/关闭Forest请求/响应日志（默认为 true）\n  log-enabled: true\n  ## 打开/关闭Forest请求日志（默认为 true）\n  log-request: true\n  ## 打开/关闭Forest响应状态日志（默认为 true）\n  log-response-status: true\n  ## 打开/关闭Forest响应内容日志（默认为 false）\n  log-response-content: true\n  variables:\n    uplatAuthUrl: http://test\n\nxxl:\n  job:\n    admin:\n      addresses: http://172.25.0.9:8080/xxl-job-admin\n    accessToken: default_token\n    executor:\n      appname: omind-scheduler-baseplat\n      address:\n      ip:\n      port: 18090\n      logpath: /data/applogs/xxl-job/jobhandler\n      logretentiondays: 30', '8c5a18e83044b6327eb3abe06613e8c2', '2025-03-14 11:41:50', '2025-03-14 11:41:50', 'nacos', '172.25.0.1', 'U', 'local', '');
