INSERT INTO `his_config_info` VALUES (1866, 2017, 'omind-mq.yml', 'DEFAULT_GROUP', '', '--- # rabbitmq 配置\nspring:\n  rabbitmq:\n    host: 172.25.0.11\n    port: 5672\n    username: omind\n    password: omind2023\n  cloud:\n    # https://docs.spring.io/spring-cloud-stream/reference/rabbit/rabbit_overview/receiving-batch.html\n    function:\n      # 重点配置 与 binding 名与消费者对应\n      definition: stationPriceSender;priceSender;realtimeData;chargeOrderData;heartBeat\n    stream:\n      function:\n        bindings:\n          realtimeData-in-0: realtimeData-in\n          realtimeData-out-0: realtimeData-out\n          chargeOrderData-in-0: chargeOrderData-in\n          chargeOrderData-out-0: chargeOrderData-out\n          priceSender-in-0: priceSender-in\n          priceSender-out-0: priceSender-out\n          stationPriceSender-in-0: stationPriceSender-in\n          stationPriceSender-out-0: stationPriceSender-out\n          heartBeat-in-0: heartBeat-in\n          heartBeat-out-0: heartBeat-out\n      default-binder: rabbit\n      bindings:\n        realtimeData-in:\n          binder: rabbit\n          destination: realtimeData-topic\n          content-type: application/json\n          group: device-group\n          consumer:\n            batch-mode: true\n            concurrency: 1 # 初始/最少/空闲时 消费者数量。默认1\n        realtimeData-out:\n          binder: rabbit\n          destination: realtimeData-topic\n          content-type: application/json\n          group: device-group\n        chargeOrderData-in:\n          binder: rabbit\n          destination: chargeOrderData-topic\n          content-type: application/json\n          group: device-group\n        chargeOrderData-out:\n          binder: rabbit\n          destination: chargeOrderData-topic\n          content-type: application/json\n          group: device-group\n        priceSender-in:\n          binder: rabbit\n          destination: priceSender-topic\n          content-type: application/json\n          group: device-group\n        priceSender-out:\n          binder: rabbit\n          destination: priceSender-topic\n          content-type: application/json\n          group: device-group\n        stationPriceSender-in:\n          binder: rabbit\n          destination: stationPriceSender-topic\n          content-type: application/json\n          group: device-group\n        stationPriceSender-out:\n          binder: rabbit\n          destination: stationPriceSender-topic\n          content-type: application/json\n          group: device-group\n        heartBeat-in:\n          binder: rabbit\n          destination: heartBeat-topic\n          content-type: application/json\n          group: device-group\n          consumer:\n            batch-mode: true\n            concurrency: 1 # 初始/最少/空闲时 消费者数量。默认1\n        heartBeat-out:\n          binder: rabbit\n          destination: heartBeat-topic\n          content-type: application/json\n          group: device-group\n      rabbit:\n        bindings:\n          realtimeData-in:\n            consumer:\n              enable-batching: true\n              batch-size: 50\n              receive-timeout: 2000\n            producer:\n              batching-enabled: true\n              batch-size: 50\n              batch-buffer-limit: 2000\n              batch-timeout: 5000\n          heartBeat-in:\n            consumer:\n              enable-batching: true\n              batch-size: 50\n              receive-timeout: 2000\n            producer:\n              batching-enabled: true\n              batch-size: 50\n              batch-buffer-limit: 2000\n              batch-timeout: 5000', '7bf66bb4ce8d9b9a86f15b62c359b09d', '2025-03-14 11:46:57', '2025-03-14 11:46:58', 'nacos', '172.25.0.1', 'U', 'local', '');
